mapper.update:

		UPDATE PUBLIC.ACCESS_TOKEN
           SET
               TOKEN             		  = #{token}
               , EXPIRATION_DATE		  = #{expirationDate}
			   , ISSUED_DATE			  = #{issuedDate}
			   , REMOTE_IP			  	  = #{remoteIP}
         WHERE USERNAME                    = #{userName}

mapper.insert:

		INSERT  
		INTO PUBLIC.ACCESS_TOKEN
          (
				USERNAME
			, TOKEN
			, ISSUED_DATE
			, EXPIRATION_DATE
			, REMOTE_IP
			, STATUS
			, USERTYPE

          )
	     VALUES
	      (
			 #{userName}
			, #{token}
			, #{issueDate}
			, #{expirationDate}
			, #{remoteIP}
			, '0'
			, #{userType}
          )
